<?php	if ( ! defined( 'ABSPATH' ) ) {		die;	}	$tour_id = $tour_id ?? get_the_id();	$age_range = TTBM_Function::get_min_age_allow( $tour_id );	$tour_type = TTBM_Function::get_tour_type( $tour_id );	$count = $count ?? 0;	if ( $age_range && $tour_type == 'general' && TTBM_Function::get_post_info( $tour_id, 'ttbm_display_min_age', 'on' ) != 'off' ) {		?>		<div class="alignCenter small_box <?php echo esc_attr( $add_class ?? '' ); ?>" data-placeholder>			<div class="item_icon"><span class="fas fa-user-circle"></span></div>			<h6>				<?php esc_html_e( 'Age Range : ', 'tour-booking-manager' ); ?>&nbsp;				<strong><?php echo esc_html( $age_range ); ?></strong>			</h6>		</div>		<?php		$count ++;	}	?>