<?php	if ( ! defined( 'ABSPATH' ) ) {		die;	}	$tour_id       = $tour_id ?? get_the_id();	$related_tours = TTBM_Function::get_related_tour( $tour_id );	if ( sizeof( $related_tours ) > 0 && TTBM_Function::get_post_info( $tour_id, 'ttbm_display_related', 'on' ) != 'off' ) {		?>		<div class='mpStyle ttbm_wraper shadow_one' id="ttbm_related_tour">			<div class="ttbm_container">				<div class='ttbm_default_widget'>					<?php do_action( 'ttbm_section_title', 'ttbm_string_related_tour', esc_html__( 'You may like Tour ', 'tour-booking-manager' ) ); ?>					<?php						if ( sizeof( $related_tours ) > 4 ) {							include( TTBM_Function::template_path( 'layout/carousel_indicator.php' ) );						}					?>					<div class="ttbm_widget_content marZero <?php if ( sizeof( $related_tours ) > 4 ) { ?> owl-theme owl-carousel <?php } else {						echo "flexWrap grid";					} ?>">						<?php foreach ( $related_tours as $tour_id ) { ?>							<div class="filter_item <?php if ( sizeof( $related_tours ) < 5 ) {								echo "grid_4";							} ?>">								<?php include( TTBM_Function::template_path( 'list/grid_list.php' ) ); ?>							</div>						<?php } ?>					</div>				</div>			</div>		</div>	<?php } ?>